import {LoadingContext} from '../store/Store';

function Loading () {
    const [loading] = useContext(LoadingContext);
    if(loading === true){
        return(
            <div class="wrapper">
            <div class="container-fluid">
                <div class="row">
                    <div class="col-12">
                        <div class="card">
                            <div class="card-body">         
                                <div>
                                    <p>Loading</p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
    </div>
        );
    } else{
        return(<div></div>)
    }

  };

export default Loading
