import React, { useContext,useEffect } from 'react';
import {LoadingContext} from './../store/Store';

function Index() {
    const [loading,setLoading] = useContext(LoadingContext);
    useEffect(() => {
        setLoading(false)
      },[]);
      if(loading === true){
        return (
            <div class="wrapper">
                <div class="container-fluid">
                    <div class="row">
                        <div class="col-12">
                            <div class="card">
                                <div class="card-body">         
                                    <div>
                                        <p>Loading</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
      } else{
        return (
            <div class="wrapper">
                <div class="container-fluid">
                    <div class="row">
                        <div class="col-12">
                            <div class="card">
                                <div class="card-body">         
                                    <div>
                                        <p>Index</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
      }

}



const LoadingPage = ({children}) => {

    return (

          {children}

    );
  };

export default Index
