import React, { useContext } from 'react';
import {LoadingContext} from './../store/Store';

function AnimatedPage ({children}) {
    const [loading] = useContext(LoadingContext);
    if(loading == true){
        return ( 
            {children}
      );
    } else{

        return(
            <div class="wrapper">
            <div class="container-fluid">
                <div class="row">
                    <div class="col-12">
                        <div class="card">
                            <div class="card-body">         
                                <div>
                                    <p>Loading</p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
    </div>
        );

    }

  };

export default AnimatedPage
